import hmac
import hashlib

def sign_payload(payload: bytes, secret: str) -> str:
    return hmac.new(
        secret.encode(),
        payload,
        hashlib.sha256
    ).hexdigest()

# Include as header:
# X-NeuraFlow-Signature: sha256=<signature>
